package com.example.pavel.recyclerview;

import android.content.Intent;
import android.nfc.Tag;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.util.Log;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {


    private static final String TAG = "Main Activity";

    // Arraylists

    private ArrayList<String> itemText = new ArrayList<>();
    private ArrayList<String> itemImages = new ArrayList<>();


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        Log.d(TAG, "On create: started");

        initImageBitmaps();
    }
    private void initImageBitmaps(){
        Log.d(TAG, "Starting itnting items");

        itemImages.add("http://bezkota.ru/wp-content/uploads/2016/03/fotograf-snimaet-primechatelnye-portrety-kotov-podcherkivayushhie-ix-lichnost-01.jpg");
        itemText.add("Кот Борис Артемович");

        itemImages.add("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqgHsAzHmQYa6dbSlLNEEpEo6r7OxR4SNmvJCjTqRWsUhON2aw1w");
        itemText.add("Васька");

        itemImages.add("data:image/jpeg;base64,");
        itemText.add("Анжелика");

        itemImages.add("http://artbs.ru/portrait/animal/kot.jpg");
        itemText.add("Сильвестор Анотонович");

        itemImages.add("https://t3.ftcdn.net/jpg/00/94/08/94/500_F_94089423_lnGSCUBKceS5CaDrHiNT6uGTKyXA2rnd.jpg");
        itemText.add("Кот Петр");


        itemImages.add("http://zakazposterov.ru/fotooboi/z_sp/fotooboi-e-36457-ruka-narisovannie-portret-kota-s-ochkami-vektor-izolirovannih-zakazposterov-ru_z_sp.jpg");
        itemText.add("Еще один кот");


        initRecyclerView();



    }
    private void initRecyclerView(){
        Log.d(TAG, "Recycler view init");

        RecyclerView recyclerView = (RecyclerView) findViewById(R.id.recycler_view);
        RecyclerViewAdapter adapter = new RecyclerViewAdapter( itemText, itemImages,this);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }
    public void goToProfile(){
        Intent intent = new Intent(this, ProfileActivity.class);
        startActivity(intent);
    }
}
